package com.melcoc.bluewhale.serviceImpl;

import com.qiniu.util.Auth;
import com.qiniu.util.StringMap;
import com.qiniu.util.UrlSafeBase64;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.RequestBody;
import org.apache.commons.codec.binary.Base64;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.UUID;

@Service
public class QiniuServiceImpl {
    @Autowired
    Qiniu qiniu;


    // 设置好账号的ACCESS_KEY和SECRET_KEY
    private String ACCESS_KEY ="KYz-15Ci2roaDFzoh7bj_vPN_4VJLVqGpsKmWApF";
    private String SECRET_KEY ="ypDlKaC4U6iev_Vg3asXRUsa1QUHdGPmLoeHlymG";
    // 要上传的空间
    private String BUCKET_NAME ="bluewhale-2020";
    // 密钥配置
    private Auth auth = Auth.create(ACCESS_KEY, SECRET_KEY);
    // 创建上传对象

    // 简单上传，使用默认策略，只需要设置上传的空间名就可以了
    public String getUpToken() {
        return auth.uploadToken(BUCKET_NAME, null, 3600, new StringMap().put("insertOnly", 1));
    }





    //base64方式上传
    public String put64image(String base64Date) throws Exception{
        //默认不指定key的情况下，以文件内容的hash值作为文件名
        String key = UUID.randomUUID().toString();
        String str=base64Date.split("base64,")[1];//去掉“data:image/jpeg”
        //解码。将base64的字符串转化二进制流
        byte[] imageByte= Base64.decodeBase64(str);
        StringBuilder buf=new StringBuilder();
        byte[] uploadBytes = imageByte;
        String file64 = com.qiniu.util.Base64.encodeToString(uploadBytes, 0);
        Integer l = uploadBytes.length;
        String url = "http://upload-z2.qiniup.com/putb64/" + l + "/key/"+  UrlSafeBase64.encodeToString(key);
        //非华东空间需要根据注意事项 1 修改上传域名
        RequestBody rb = RequestBody.create(null, file64);
        Request request1 = new Request.Builder().
                url(url).
                addHeader("Content-Type", "application/octet-stream")
                .addHeader("Authorization", "UpToken " + getUpToken())
                .post(rb).build();
        //System.out.println(request.headers());
        OkHttpClient client = new OkHttpClient();
        okhttp3.Response response1 = client.newCall(request1).execute();
        System.out.println(response1);
       // 如果不需要添加图片样式，使用以下方式
        System.out.println(key);
        System.out.println(qiniu.getUrl(0,key));
        return  key;
    }


    public static void main(String[] args) throws Exception {
        Qiniu qiniu=new Qiniu();
        QiniuServiceImpl qiniuService=new QiniuServiceImpl();
        System.out.println(qiniu.getUrl(0, qiniuService.put64image("data:image/jpg;base64,")
        ));
    }
}
